import React from 'react'
import { LinkedList } from './LinkedList'

export class LinkedListComp extends React.Component {
    constructor() {
        super();
        this.new = this.new.bind(this)
        this.show = this.show.bind(this)
        this.add = this.add.bind(this)
        this.ll = null
        this.state = {
            subject: "",
            amount: ""
        }
    }

    new() {
        this.ll = new LinkedList(this.state.subject, this.state.amount)
        this.setState({
            subject: "",
            amount: ""
        })
        this.clearInputs();
    }

    add() {
        this.ll.add(this.state.subject, this.state.amount)
        this.setState({
            subject: "",
            amount: ""
        })
        this.clearInputs();
    }

    clearInputs() {
        document.getElementById("newSubject").value = ''
        document.getElementById("newAmount").value = ''
    }

    show() {
        console.log(this.ll)
        let display = document.getElementById('showlist')
        display.innerHTML = this.ll.show();
   }

    onSubjectChange(e) {
        this.setState({
            subject: e.target.value
        })
    }

    onAmountChange(e) {
        this.setState({
            amount: e.target.value
        })
    }

    render() {
        return(
            <div>
                <button id="new" onClick={this.new}>New</button>
                <button id="add" onClick={this.add}>Add</button>
                <br />
                <input id="newSubject" onChange={(e) => this.onSubjectChange(e)} type='text' />
                <input id="newAmount" type='text' onChange={(e) => this.onAmountChange(e)}/>
                <br />
                <button id="showall" onClick={this.show}>Show All</button>
                <div id="showlist" className=""> </div>
            </div>
        )
    }
}
