import React from 'react'
import { LinkedList } from './LinkedList'

export class LinkedListComp extends React.Component {
    constructor() {
        super();
        this.ll = null
        this.state = {
            subject: "",
            amount: "",
            currentIndex: 0,
            node: "null" 
        }
    }

    new = () => {
        this.ll = new LinkedList(this.state.subject, this.state.amount)
        this.setState({
            subject: "",
            amount: "",
            currentIndex: 1,
            node: `subject=${this.ll.head.subject}, amount=${this.ll.head.amount}`
        })
        this.clearInputs();
    }



    insert = () => {
        let nextIndex = this.state.currentIndex;
        nextIndex++;
       this.ll.insert(nextIndex, this.state.subject, this.state.amount)
       console.log(nextIndex)
       let nextNode = this.ll.get(nextIndex)
       console.log(this.ll)
       console.log(nextNode)
       this.setState({
            subject: "",
            amount: "",
            currentIndex: nextIndex,
            node: `subject=${"asdf"}, amount=${"asdf"}`
       })
    }

    add = () => {
        this.ll.push(this.state.subject, this.state.amount)
        let newIndex = this.state.currentIndex;
        newIndex++;
        this.setState({
            subject: "",
            amount: "",
            currentIndex: newIndex,
            node: `subject=${this.ll.tail.subject}, amount=${this.ll.tail.amount}` 
        })
        console.log(this.state.currentIndex)
        this.clearInputs();
    }

    clearInputs = () => {
        document.getElementById("newSubject").value = ''
        document.getElementById("newAmount").value = ''
    }

    show = () => {
        console.log(this.ll)
        let display = document.getElementById('showlist')
        display.innerHTML = this.state.display
   }

    onSubjectChange = (e) => {
        this.setState({
            subject: e.target.value
        })
    }

    onAmountChange = (e) => {
        this.setState({
            amount: e.target.value
        })
    }

    render() {
        return(
            <div>
                <button id="new" onClick={this.new}>New</button>
                <button id="add" onClick={this.add}>Add</button>
                <button id="add" onClick={this.insert}>Insert</button>
                <button id="add" onClick={this.delete}>Delete</button>
                <br />
                <input id="newSubject" className="inputs" type='text' placeholder="Subject" onChange={(e) => this.onSubjectChange(e)} />
                <input id="newAmount" className="inputs" type='text' placeholder="Amount" onChange={(e) => this.onAmountChange(e)}/>
                <br />
                <Display index={this.state.currentIndex} node={this.state.node}/>
                <button id="first" onClick={this.first}>First</button>
                <button id="prev" disabled onClick={this.prev}>Prev</button>
                <button id="next" onClick={this.next}>Next</button>
                <button id="last" onClick={this.last}>Last</button>
            </div>
        )
    }
}

//                <div id="showlist" className="">{this.state.display}</div>

function Display(props) {
    return (
        <div className="display">
            <div className="display-inner display-index">{props.index}</div>
            <div className="display-inner display-node">{props.node}</div>
        </div>
    )
}
