import React from 'react'
import { LinkedList } from './LinkedList'

export class LinkedListComp extends React.Component {
    constructor() {
        super();
        this.new = this.new.bind(this)
        this.ll = null
        this.state = {
            subject: "",
            amount: ""
        }
    }

    new(e) {
        this.ll = new LinkedList(this.state.subject, this.state.amount)
        this.setState({
            subject: "",
            amount: ""
        })
    }

    show(e) {
        let display = document.getElementById('showlist')
        display.innerHTML = this.ll.show();
   }

    onSubjectChange(e) {
        this.setState({
            subject: e.target.value
        })
    }

    onAmountChange(e) {
        this.setState({
            amount: e.target.value
        })
    }

    render() {
        return(
            <div>
                <h1>Hello World!</h1>
                <button id="new" onClick={this.new}>New</button>
                <input id="newSubject" onChange={this.onSubjectChange}type='text' />
                <br />
                <button id="add">Add</button>
                <input id="newAmount" type='text' onChange={this.onAmountChange}/>
                <br />
                <button id="showall" onClick="show">Show All</button>
                <div id="showlist" className=""> </div>
            </div>
        )
    }
}
