export class Node {
    constructor(subject, amount) {
        this.subject = subject;
        this.amount = amount;
        this.forwardNode = null;
    }
}

export class LinkedList {
    constructor(subject, amount) {
        this.head = new Node(subject, amount)
        this.showString = this.head.subject + " : " + this.head.amount;
        this.length = 0;
    }

    show() {
            return this.showString;
    }

    add(subject, amount) {
        const node = new Node(subject, amount);
        let currentNode = null;

        if (!this.head) {
          this.head = node;
        } else {
          currentNode = this.head;
          while (currentNode.forwardNode) {
            currentNode = currentNode.forwardNode;
          }
          currentNode.forwardNode = node;
        }
        this.showString += ', ' + node.subject + " : " + node.amount;
        this.length = this.length + 1;
      }
}
